import React, { useState } from 'react';
import '../App.css';
import Header from './Header';
import Keypad from './Keypad';

function App() {
  const data = [
    {id:"1",value:"1"},
    {id:"2",value:"2"},
    {id:"3",value:"3"},
    {id:"4",value:"4"},
    {id:"5",value:"5"},
    {id:"6",value:"6"},
    {id:"7",value:"7"},
    {id:"8",value:"8"},
    {id:"9",value:"9"},
    {id:"0",value:"0"},
    {id:"add",value:"+"},
    {id:"sub",value:"-"},
    {id:"multi",value:"*"},
    {id:"div",value:"/"},
    {id:"equal",value:"="},
    {id:"deci",value:"."},
    ]

    const [keyInput,setKeyInput] = useState("");
    const setInput = (e) => {
        setKeyInput(e.target.value)
    }
    const handleChange =(handleChange) => {
      if(handleChange!=="="){
        setKeyInput(prevState => prevState + handleChange );
      }
      else{
        try{
          setKeyInput(
           (new Function('return ' + keyInput)().length > 3) &&
           (new Function('return ' + keyInput)().includes("."))
              ?(new Function('return ' + keyInput)().toFixed(4))
              :(new Function('return ' + keyInput)())
          );     
        } catch(e){
          console.log(e);
        }

      }
    }


  return (

    <div className="App">
        <Header title="Calculator App"/>
        <div className="keypad">
        <div className="key-input">
        <input type="text" name="keyinput" value={keyInput} onChange={setInput} />
        </div>
        {data.map((val)=>(
          <Keypad id={val.id} value={val.value} handleChange={handleChange}/>
        ))}
        </div>

    </div>
  );
}

export default App;
