import React, { useState } from 'react';
import '../App.css';
import Header from './Header';
import Keypad from './Keypad';
import { evaluate } from 'mathjs'



function App() {
  const data = [
    {id:"1",value:"1"},
    {id:"2",value:"2"},
    {id:"3",value:"3"},
    {id:"4",value:"4"},
    {id:"5",value:"5"},
    {id:"6",value:"6"},
    {id:"7",value:"7"},
    {id:"8",value:"8"},
    {id:"9",value:"9"},
    {id:"0",value:"0"},
    {id:"add",value:"+"},
    {id:"sub",value:"-"},
    {id:"multi",value:"*"},
    {id:"div",value:"/"},
    {id:"deci",value:"."},
    {id:"equal",value:"="},
    {id:"clear",value:"Clear"},
    {id:"AC",value:"AC"}
    ]

    const [keyInput,setKeyInput] = useState("");

    const setInput = (e) => {
        setKeyInput(e.target.value)
    }
    const handleChange =(handleChange) => {
      switch(handleChange) {
        case '=':
            try{
              setKeyInput(evaluate(keyInput));     
            } catch(e){
              alert("error");
            }
            break;
        case "clear":
            if(keyInput.length>0) 
              setKeyInput(keyInput.substring(0, keyInput.length - 1));
            break;
        case "AC":
            setKeyInput("");
            break;
        default:
            setKeyInput(prevState => prevState + handleChange );
            break;
      }

    }

  return (

    <div className="App">
        <Header title="Calculator App"/>
        <div className="keypad">
        <div className="key-input">
        <input type="text" name="keyinput" value={keyInput} onChange={setInput} />
        </div>
        {data.map((val)=>(
          <Keypad key={val.id} id={val.id} value={val.value} handleChange={handleChange}/>
        ))}
        </div>

    </div>
  );
}

export default App;
